import React, { use, useState } from "react";

function GeneradorTextoChatbot(){
  const[promp,setPrompt]= useState("");
  const[conversation, setConversation]= useState("");
  const[isloading, setIsLoading]= useState("");
  const[errorMessage, setErrorMessage]= useState("");
  const generateText = async (e)=>{
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
  
    try{

      const response = await fetch("http://localhost:3001/generateText",{
        method:  "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({promt: prompt})
      });
      
      if(!response.ok){
        throw new Error(`HTTP error`)
      }
      const data = await response.json();
      const respuestaChatGPT = data.respuesta; 
      setConversation(prev =>[
        ...prev,
        {sender: "User", text: prompt},
        {sender: "ChatGPT", text:respuestaChatGPT}
      ])

    }catch(error){
      console.error("error")
      setErrorMessage("Error en la solicitud")
    }finally{
      setIsLoading(false);
    }
  };
return(
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.role === "user" ? "user" : "bot"}`}
          >
            {msg.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit} className="input-form">
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Escribe tu mensaje..."
        />
        <button type="submit">Enviar</button>
      </form>
    </div>
)


}
export default GeneradorTextoChatbot;
