import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Estado para manejar errores
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = (email) => /^[^@]+@[^@]+\.[^@]+$/.test(email);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos
    setIsLoading(true);

    // Validaciones del frontend
    if (!email || !password) {
      setError("Por favor completa todos los campos");
      setIsLoading(false);
      return;
    }

    if (!isEmailValid(email)) {
      setError("Por favor ingresa un correo válido");
      setIsLoading(false);
      return;
    }

    try {
      const dispositivo = navigator.userAgent;
      const canal = "web";

      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, dispositivo, canal }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || "Error desconocido al iniciar sesión");
        setIsLoading(false);
        return;
      }

      if (data.success) {
        // Guarda la sesión del usuario
        localStorage.setItem("id_sesion", data.id_sesion);
        localStorage.setItem("nombre_usuario", data.nombre);
        localStorage.setItem("role", data.rol);

        if (data.rol === "admin") {
          window.location.href = `http://localhost:3000/menu_admin?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
            data.nombre
          )}&role=${encodeURIComponent(data.rol)}`;
        } else {
          window.location.href = `http://localhost:3000/menu_usuario?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
            data.nombre
          )}&role=${encodeURIComponent(data.rol)}`;
        }
      }
    } catch (err) {
      setError("Error al conectar con el servidor");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-br from-gray-100 via-gray-300 to-gray-200">
      {/* Contenedor del Login */}
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-sm">
        {/* Logo */}
        <div className="flex justify-center mb-4">
          <img
            src="/logo.webp" // Cambia esta ruta a la ubicación de tu logo
            alt="Logo"
            className="w-16 h-16" // Ajusta el tamaño según sea necesario
          />
        </div>

        {/* Título */}
        <h2 className="text-lg font-semibold text-gray-700 text-center mb-4">
          Iniciar Sesión
        </h2>

        {/* Mensaje de error */}
        {error && (
          <p className="text-red-600 text-sm text-center mb-4">{error}</p>
        )}

        {/* Formulario */}
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            placeholder="Correo"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <input
            type="password"
            placeholder="Contraseña"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <button
            type="submit"
            className={`w-full py-2 text-white font-bold rounded-md ${
              isLoading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-indigo-600 hover:bg-indigo-700"
            }`}
            disabled={isLoading}
          >
            {isLoading ? "Cargando..." : "Iniciar Sesión"}
          </button>
        </form>

        {/* Enlaces */}
        <div className="flex justify-between text-sm text-indigo-600 mt-4">
          <button
            onClick={() => navigate("/register")}
            className="hover:underline"
          >
            ¿Primera vez? Regístrese
          </button>
          <a href="/forgot-password" className="hover:underline">
            ¿Olvidó su contraseña?
          </a>
        </div>
      </div>
    </div>
  );
}

export default Login;
