import React, { useState, useEffect } from "react";
import "./GeneradorTextoChatbot.css";

function GeneradorTextoChatbot() {
  const [prompt, setPrompt] = useState("");
  const [conversation, setConversation] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [graphUrl, setGraphUrl] = useState("");
  const [sessionId, setSessionId] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");
  

  // Obtener el session_id desde la URL
  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const session_id = queryParams.get("session_id");
    if (session_id) {
      setSessionId(session_id);
    } else {
      console.error("No se proporcionó un session_id en la URL.");
      window.location.href = "http://localhost:3000";
    }
    const nombre = localStorage.getItem("nombre_usuario");
    if (nombre) {
      setNombreUsuario(nombre);
    }
  }, []);

  // Obtener la URL de la gráfica al cargar el componente
  useEffect(() => {
    if (sessionId) {
      setGraphUrl(`http://localhost:5000/api/user-interactions?session_id=${sessionId}`);
    }
  }, [sessionId]);

  // Cargar conversación desde localStorage
  useEffect(() => {
    const savedConversation = localStorage.getItem("conversation");
    if (savedConversation) {
      setConversation(JSON.parse(savedConversation));
    }
  }, []);

  // Guardar conversación en localStorage
  useEffect(() => {
    localStorage.setItem("conversation", JSON.stringify(conversation));
  }, [conversation]);

  const generateText = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await fetch("http://localhost:5000/api/chatbot", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ prompt, id_sesion: sessionId }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.error || "Error desconocido en el servidor");
        return;
      }

      const data = await response.json();
      const respuestaChatGPT = data.response;

      setConversation((prev) => [
        ...prev,
        { sender: nombreUsuario, text: prompt },
        { sender: "Chatbot", text: respuestaChatGPT },
      ]);
    } catch (error) {
      console.error("Error en la solicitud:", error);
      setErrorMessage("Error en la solicitud al servidor");
    } finally {
      setIsLoading(false);
      setPrompt("");
    }
  };

  const handleLogout = async () => {
    if (!sessionId) {
      console.error("No se encontró un session_id en la URL.");
      setErrorMessage("No se encontró una sesión activa.");
      return;
    }
  
    try {
      const response = await fetch("http://localhost:5000/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id_sesion: sessionId }), // Aquí debe coincidir con lo que espera el backend
      });
  
      if (response.ok) {
        console.log("Sesión cerrada correctamente.");
        localStorage.removeItem("id_sesion");
        window.location.href = "http://localhost:3000/login";
      } else {
        const data = await response.json();
        setErrorMessage(data.message || "No se pudo cerrar la sesión.");
      }
    } catch (error) {
      console.error("Error en la solicitud de logout:", error);
      setErrorMessage("Error en la solicitud al servidor para cerrar sesión.");
    }
    setSessionId("");
    setConversation([]);
  };
  
  return (
    <div className="chat-container">
      <h2>Bienvenido, {nombreUsuario}</h2>
      <div className="logout-container">
        <button onClick={handleLogout} className="logout-button">
          Cerrar Sesión
        </button>
      </div>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <div className="chat-box">
        {conversation.map((msg, index) => (
          <div key={index} className={`message ${msg.sender === nombreUsuario ? "user" : "bot"}`}>
            <strong>{msg.sender}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={generateText} className="input-form">
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Escribe tu mensaje..."
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Enviando..." : "Enviar"}
        </button>
      </form>
      {graphUrl.length > 0 && (
        <div className="graph-container">
          <h2>Estadísticas de Interacciones</h2>
          <table>
            <thead>
              <tr>
                <th>Tipo de Mensaje</th>
                <th>Cantidad</th>
              </tr>
            </thead>
            <tbody>
              {graphUrl.map((interaction, index) => (
                <tr key={index}>
                  <td>{interaction.tipo_mensaje}</td>
                  <td>{interaction.cantidad}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default GeneradorTextoChatbot;
