import React, { useState, useEffect } from "react";
import "./GeneradorTextoChatbot.css";

function GeneradorTextoChatbot() {
  const [prompt, setPrompt] = useState("");
  const [conversation, setConversation] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [sessionId, setSessionId] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");

  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const session_id = queryParams.get("session_id");
    const nombre = queryParams.get("nombre_usuario");
    if (session_id) {
      setSessionId(session_id);
    } else {
      setErrorMessage("No se encontró una sesión activa.");
      window.location.href = "http://localhost:3000/login";
    }
    if (nombre) {
      setNombreUsuario(nombre);
    }
  }, []);

  const generateText = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await fetch("http://localhost:5000/api/chatbot", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          prompt,
          id_sesion: sessionId,
          nombre_usuario: nombreUsuario,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.error || "Error desconocido en el servidor");
        return;
      }

      const data = await response.json();
      const respuestaChatGPT = data.response;

      setConversation((prev) => [
        ...prev,
        { sender: nombreUsuario, text: prompt },
        { sender: "Chatbot", text: respuestaChatGPT },
      ]);
    } catch (error) {
      setErrorMessage("Error en la solicitud al servidor");
    } finally {
      setIsLoading(false);
      setPrompt("");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("id_sesion");
    localStorage.removeItem("nombre_usuario");
    window.location.href = "http://localhost:3000/login";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-indigo-600 flex flex-col items-center p-6">
      {/* Botón de Cerrar Sesión */}
      <button
        onClick={handleLogout}
        className="absolute top-4 right-4 bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded shadow-lg"
      >
        Cerrar Sesión
      </button>

      {/* Contenedor del Chatbot */}
      <div className="bg-white w-full max-w-lg rounded-lg shadow-md p-6 flex flex-col">
        {/* Título */}
        <h2 className="text-xl font-bold text-gray-800 text-center mb-4">
          Chatbot Interactivo
        </h2>

        {/* Caja de Mensajes */}
        <div className="flex-grow overflow-y-auto h-72 border border-gray-200 rounded-lg p-4 bg-gray-50">
          {conversation.map((msg, index) => (
            <div
              key={index}
              className={`p-3 rounded-lg mb-2 max-w-sm ${
                msg.sender === nombreUsuario
                  ? "bg-blue-500 text-white self-end ml-auto"
                  : "bg-gray-200 text-gray-800 self-start"
              }`}
            >
              <strong>{msg.sender}:</strong> {msg.text}
            </div>
          ))}
        </div>

        {/* Indicador de error */}
        {errorMessage && (
          <div className="text-red-500 text-sm mt-2">{errorMessage}</div>
        )}

        {/* Entrada de Texto */}
        <form
          onSubmit={generateText}
          className="mt-4 flex items-center gap-4"
        >
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Escribe tu mensaje..."
            className="flex-grow px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
            required
            disabled={isLoading}
          />
          <button
            type="submit"
            className={`px-6 py-2 rounded-lg font-bold text-white ${
              isLoading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-blue-600 hover:bg-blue-700"
            }`}
            disabled={isLoading}
          >
            {isLoading ? "Cargando..." : "Enviar"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default GeneradorTextoChatbot;
