import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Validar formato de correo electrónico
  const isEmailValid = (email) => {
    const emailRegex = /^[^@]+@[^@]+\.[^@]+$/;
    return emailRegex.test(email);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos

    // Validaciones del frontend
    if (!email || !password) {
      setError("Por favor completa todos los campos");
      return;
    }

    if (!isEmailValid(email)) {
      setError("Por favor ingresa un correo válido");
      return;
    }

    setIsLoading(true); // Mostrar indicador de carga

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          email,
          password,
          dispositivo: navigator.userAgent,
          canal: "web",
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        // Mostrar el mensaje de error del backend
        setError(data.message || "Error desconocido al iniciar sesión");
        setIsLoading(false);
        return;
      }

      if (data.success) {
        // Guardar datos en localStorage
        localStorage.setItem("id_sesion", data.id_sesion);
        localStorage.setItem("nombre_usuario", data.nombre);

        // Redirigir al componente principal del chatbot
        window.location.href = `http://localhost:3002?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(data.nombre)}`;
      }
    } catch (err) {
      setError("Error al conectar con el servidor");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>} {/* Mostrar mensaje de error */}
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Correo"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Cargando..." : "Iniciar Sesión"}
        </button>
      </form>
      <button onClick={() => navigate("/register")}>Registrarse</button>
    </div>
  );
}

export default Login;
