import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../App";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { setIsAuthenticated } = useAuth(); // Usar contexto global

  const handleLogin = async (e) => {
    e.preventDefault();
    const dispositivo = navigator.userAgent;
    const canal = "web";

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, dispositivo, canal }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Error en el servidor");
      }

      const data = await response.json();
      if (data.success) {
        localStorage.setItem("id_sesion", data.id_sesion);
        localStorage.setItem("nombre_usuario", data.nombre);

        // Actualizar el estado de autenticación
        setIsAuthenticated(true);

        // Redirigir al chatbot
        navigate(`/chat?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(data.nombre)}`);
      } else {
        setError(data.message || "Credenciales incorrectas");
      }
    } catch (err) {
      setError(err.message || "Error al iniciar sesión");
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Correo"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
          required
        />
        <button type="submit">Iniciar Sesión</button>
      </form>
      <button onClick={() => navigate("/register")}>Registrarse</button>
    </div>
  );
}

export default Login;
