import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/login.css"

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false); // Estado para controlar el modal
  const navigate = useNavigate();

  const isEmailValid = (email) => /^[^@]+@[^@]+\.[^@]+$/.test(email);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setIsModalVisible(false);

    if (!email || !password) {
      setError("Por favor completa todos los campos");
      return;
    }

    if (!isEmailValid(email)) {
      setError("Por favor ingresa un correo válido");
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (data.success) {
        // Mostrar modal de éxito
        setIsModalVisible(true);
        setTimeout(() => {
          // Redirigir según el rol después de mostrar el modal
          if (data.rol === "admin") {
            navigate(
              `/menu_admin?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
                data.nombre
              )}&role=${encodeURIComponent(data.rol)}`
            );
          } else {
            navigate(
              `/menu_usuario?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
                data.nombre
              )}&role=${encodeURIComponent(data.rol)}`
            );
          }
        }, 3000); // Redirigir después de 3 segundos
      } else {
        setError(data.message || "Error al iniciar sesión");
      }
    } catch (err) {
      setError("Error al conectarse al servidor");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container flex items-center justify-center min-h-screen bg-gradient-to-br from-gray-100 via-gray-300 to-gray-200">
      {/* Modal */}
      {isModalVisible && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full">
            <h2 className="text-lg font-bold text-green-600 mb-4">
              ¡Inicio de Sesión Exitoso!
            </h2>
            <p className="text-gray-700 mb-6">
              Redirigiendo a tu menú principal. Por favor, espera un momento.
            </p>
            <button
              onClick={() => setIsModalVisible(false)}
              className="w-full py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
            >
              Cerrar
            </button>
          </div>
        </div>
      )}
       {/* Logo centrado */}
      <div className="logo-container">
        <img
           src="/Logo.webp" // Asegúrate de que esta ruta sea correcta
           alt="Logo"
           className="logo"
        />
      </div>
      {/* Contenedor Principal */}
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-semibold text-gray-800 text-center mb-6">
          Iniciar Sesión
        </h2>

        {/* Alerta de Error */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4 flex items-center">
            <svg
              className="w-5 h-5 mr-2"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M13 16h-1v-4h-1m0-4h.01M12 2a10 10 0 100 20 10 10 0 000-20z"
              />
            </svg>
            <span>{error}</span>
          </div>
        )}

        {/* Formulario */}
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            placeholder="Correo"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <button
            type="submit"
            className={`w-full py-2 text-white font-bold rounded-md ${
              isLoading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-indigo-600 hover:bg-indigo-700"
            }`}
            disabled={isLoading}
          >
            {isLoading ? "Cargando..." : "Iniciar Sesión"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
