import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const dispositivo = navigator.userAgent; // Información del dispositivo
    const canal = "web"; // Canal fijo como ejemplo
  
    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, dispositivo, canal }),
      });
      const data = await response.json();
      if (data.success) {
        // Guarda la sesión del usuario
        localStorage.setItem("id_sesion", data.id_sesion);
  
        // Redirige al frontend del chatbot (puerto 3002)
        window.location.href = "http://localhost:3002";
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("Error al iniciar sesión");
    }
  };
  

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="Correo" onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Contraseña" onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Iniciar Sesión</button>
      </form>
      <button onClick={() => navigate("/register")}>Registrarse</button>
    </div>
  );
}

export default Login;
