import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = (email) => /^[^@]+@[^@]+\.[^@]+$/.test(email);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    if (!email || !password) {
      setError("Por favor completa todos los campos");
      setIsLoading(false);
      return;
    }

    if (!isEmailValid(email)) {
      setError("Por favor ingresa un correo válido");
      setIsLoading(false);
      return;
    }

    try {
      const dispositivo = navigator.userAgent;
      const canal = "web";

      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, dispositivo, canal }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || "Error desconocido al iniciar sesión");
        setIsLoading(false);
        return;
      }

      if (data.success) {
        localStorage.setItem("id_sesion", data.id_sesion);
        localStorage.setItem("nombre_usuario", data.nombre);
        localStorage.setItem("role", data.rol);

        if (data.rol === "admin") {
          navigate(
            `/menu_admin?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
              data.nombre
            )}&role=${encodeURIComponent(data.rol)}`
          );
        } else {
          navigate(
            `/menu_usuario?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(
              data.nombre
            )}&role=${encodeURIComponent(data.rol)}`
          );
        }
      }
    } catch (err) {
      setError("Error al conectar con el servidor");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-logo">
          <img src="/logo.png" alt="Logo" /> {/* Cambia por tu logo */}
        </div>

        <form onSubmit={handleLogin} className="login-form">
          <h2 className="login-title">Iniciar Sesión</h2>

          {error && <p className="login-error">{error}</p>}

          <div className="form-group">
            <input
              type="email"
              placeholder="Correo"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="password"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="login-button" disabled={isLoading}>
            {isLoading ? "Cargando..." : "Iniciar Sesión"}
          </button>
        </form>

        <div className="login-links">
          <a href="/register" className="register-link">
            ¿Primera vez? Regístrese
          </a>
          <a href="/forgot-password" className="forgot-link">
            ¿Olvidó su contraseña?
          </a>
        </div>

        <div className="login-footer">
          <a href="/analyst-login" className="analyst-link">
            ¿Eres analista?
          </a>
        </div>
      </div>
    </div>
  );
}

export default Login;
