import React, { useState, useEffect } from "react";

function MisInteracciones() {
    const [interacciones, setInteracciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchInteracciones = async () => {
            const idUsuario = localStorage.getItem("id_usuario"); // Obtén el id_usuario del localStorage
            if (!idUsuario) {
                setError("No se encontró un usuario válido");
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/mis-interacciones?id_usuario=${idUsuario}`);
                const data = await response.json();

                if (response.ok) {
                    setInteracciones(data.interacciones || []);
                } else {
                    setError(data.message || "Error al obtener interacciones");
                }
            } catch (err) {
                setError("Error al conectar con el servidor");
            } finally {
                setLoading(false);
            }
        };

        fetchInteracciones();
    }, []);

    if (loading) return <p>Cargando...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h1>Mis Interacciones</h1>
            <ul>
                {interacciones.map((interaccion, index) => (
                    <li key={index}>
                        <strong>Sesión:</strong> {interaccion.id_sesion} <br />
                        <strong>{interaccion.tipo_mensaje}:</strong> {interaccion.mensaje} <br />
                        <small>{interaccion.timestamp}</small>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default MisInteracciones;
