import React from "react";
import { useNavigate } from "react-router-dom";

function MenuUsuario() {
  const navigate = useNavigate();
  const sessionId = localStorage.getItem("id_sesion");

  const handleLogout = async () => {
    try {
      await fetch("http://localhost:5000/logout", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id_sesion: sessionId }),
      });
      localStorage.removeItem("id_sesion");
      localStorage.removeItem("nombre_usuario");
      localStorage.removeItem("role");
      window.location.href = "/login";
    } catch (err) {
      console.error("Error al cerrar sesión:", err);
    }
  };

  const handleChatbotRedirect = () => {
    const sessionId = localStorage.getItem("id_sesion");
    const userName = localStorage.getItem("nombre_usuario");
    const rol = localStorage.getItem("role");
    if (sessionId && userName) {
      window.location.href = `http://localhost:3002?session_id=${sessionId}&nombre_usuario=${encodeURIComponent(
        userName
      )}&role=${encodeURIComponent(rol)}`;
    } else {
      alert("No se encuentra la sesión, por favor inicie sesión.");
    }
  };

  return (
    <div className="menu-container min-h-screen bg-gradient-to-br from-blue-500 to-indigo-700 flex flex-col items-center justify-center text-white">
      <h1 className="text-4xl font-bold mb-8">Menú Usuario</h1>
      <div className="menu-options bg-white shadow-lg rounded-lg p-8 max-w-md w-full text-gray-800">
        <ul className="space-y-4">
          <li>
            <button
              onClick={() => navigate("/mis-interacciones")}
              className="menu-button"
            >
              Revisar Mis Interacciones
            </button>
          </li>
          <li>
            <button onClick={handleChatbotRedirect} className="menu-button">
              Interactuar con Chatbot
            </button>
          </li>
          <li>
            <button
              onClick={() => navigate("/gestionar-cuenta")}
              className="menu-button"
            >
              Gestionar Mi Cuenta
            </button>
          </li>
          <li>
            <button onClick={handleLogout} className="menu-button">
              Cerrar Sesión
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default MenuUsuario;
