import React, { useState, useEffect } from "react";
import { FaHome, FaSignOutAlt, FaUserEdit, FaTrashAlt, FaLock } from "react-icons/fa";
import "../styles/gestionar_usuario.css"; // Archivo CSS personalizado

function GestionarUsuario() {
    const [usuario, setUsuario] = useState({ nombre: "", email: "" });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");
    const [password, setPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [modalContent, setModalContent] = useState({ message: "", type: "" });

    useEffect(() => {
        const fetchUsuario = async () => {
            const idSesion = localStorage.getItem("id_sesion");
            if (!idSesion) {
                setError("No se encontró una sesión activa");
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/usuario?id_sesion=${idSesion}`);
                const data = await response.json();

                if (response.ok) {
                    setUsuario(data.usuario);
                } else {
                    setError(data.message || "Error al obtener datos del usuario");
                }
            } catch (err) {
                setError("Error al conectar con el servidor");
            } finally {
                setLoading(false);
            }
        };

        fetchUsuario();
    }, []);

    const handleUpdate = async (e) => {
        e.preventDefault();
        setError("");
        setSuccess("");

        try {
            const response = await fetch("http://localhost:5000/usuario", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ ...usuario, id_sesion: localStorage.getItem("id_sesion") }),
            });

            if (response.ok) {
                setSuccess("Datos actualizados correctamente");
            } else {
                const data = await response.json();
                setError(data.message || "Error al actualizar datos");
            }
        } catch (err) {
            setError("Error al conectar con el servidor");
        }
    };

    const handleUpdatePassword = async (e) => {
        e.preventDefault();

        if (newPassword !== confirmPassword) {
            showModal("Las contraseñas no coinciden", "error");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/usuario", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    id_sesion: localStorage.getItem("id_sesion"),
                    password: password,
                    new_password: newPassword,
                }),
            });

            if (response.ok) {
                showModal("Contraseña actualizada correctamente", "success");
                setPassword("");
                setNewPassword("");
                setConfirmPassword("");
            } else {
                const data = await response.json();
                showModal(data.error || "Error al actualizar la contraseña", "error");
            }
        } catch (err) {
            showModal("Error al conectar con el servidor", "error");
        }
    };

    const showModal = (message, type) => {
        setModalContent({ message, type });
        setIsModalVisible(true);

        // Ocultar el modal automáticamente después de 3 segundos
        setTimeout(() => {
            setIsModalVisible(false);
        }, 2000);
    };
    

    const handleDelete = async () => {
        if (window.confirm("¿Estás seguro de que deseas eliminar tu cuenta? Esta acción no se puede deshacer.")) {
            try {
                const idSesion = localStorage.getItem("id_sesion");
                const response = await fetch(`http://localhost:5000/usuario?id_sesion=${idSesion}`, {
                    method: "DELETE",
                });

                if (response.ok) {
                    localStorage.clear();
                    window.location.href = "/login";
                } else {
                    const data = await response.json();
                    setError(data.message || "Error al eliminar la cuenta");
                }
            } catch (err) {
                setError("Error al conectar con el servidor");
            }
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("id_sesion");
        window.location.href = "/login";
    };

    const handleMenu = () => {
        window.location.href = "/menu_usuario";
    };

    if (loading) return <p>Cargando...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div className="gestionar-usuario-container">
            {/* Menú lateral */}
            <aside className="sidebar">
                <h2 className="sidebar-title">Opciones</h2>
                <button onClick={handleMenu} className="sidebar-button">
                    <FaHome className="sidebar-icon" /> Menú Principal
                </button>
                <button onClick={handleLogout} className="sidebar-button">
                    <FaSignOutAlt className="sidebar-icon" /> Cerrar Sesión
                </button>
            </aside>

            {/* Formulario de Gestión */}
            <div className="usuario-box">
                <h1 className="usuario-title">Gestionar Usuario</h1>

                {success && <p className="success-message">{success}</p>}
                {error && <p className="error-message">{error}</p>}

                {/* Formulario para actualizar datos */}
                <form onSubmit={handleUpdate} className="usuario-form">
                    <label>
                        Nombre:
                        <input
                            type="text"
                            value={usuario.nombre}
                            onChange={(e) => setUsuario({ ...usuario, nombre: e.target.value })}
                            required
                        />
                    </label>
                    <label>
                        Email:
                        <input
                            type="email"
                            value={usuario.email}
                            onChange={(e) => setUsuario({ ...usuario, email: e.target.value })}
                            required
                        />
                    </label>
                    <button type="submit" className="usuario-button">
                        <FaUserEdit /> Actualizar Datos
                    </button>
                </form>

                {/* Formulario para cambiar contraseña */}
                <form onSubmit={handleUpdatePassword} className="password-form">
                    <label>
                        Contraseña Actual:
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Nueva Contraseña:
                        <input
                            type="password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Confirmar Nueva Contraseña:
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                        />
                    </label>
                    <button type="submit" className="usuario-button">
                        <FaLock /> Cambiar Contraseña
                    </button>
                </form>

                <button onClick={handleDelete} className="usuario-button delete-button">
                    <FaTrashAlt /> Eliminar Cuenta
                </button>
            </div>
        </div>
    );
}

export default GestionarUsuario;
