import React, { useState, useEffect } from "react";
import "./GeneradorTextoChatbot.css";

function GeneradorTextoChatbot() {
  const [prompt, setPrompt] = useState("");
  const [conversation, setConversation] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [sessionId, setSessionId] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");

  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const session_id = queryParams.get("session_id");
    const nombre = queryParams.get("nombre_usuario");
    if (session_id) {
      setSessionId(session_id);
    } else {
      setErrorMessage("No se encontró una sesión activa.");
      window.location.href = "http://localhost:3000/login";
    }
    if (nombre) {
      setNombreUsuario(nombre);
    }
  }, []);

  const generateText = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await fetch("http://localhost:5000/api/chatbot", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          prompt,
          id_sesion: sessionId,
          nombre_usuario: nombreUsuario,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.error || "Error desconocido en el servidor");
        return;
      }

      const data = await response.json();
      const respuestaChatGPT = data.response;

      setConversation((prev) => [
        ...prev,
        { sender: nombreUsuario, text: prompt },
        { sender: "Chatbot", text: respuestaChatGPT },
      ]);
    } catch (error) {
      setErrorMessage("Error en la solicitud al servidor");
    } finally {
      setIsLoading(false);
      setPrompt("");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("id_sesion");
    localStorage.removeItem("nombre_usuario");
    window.location.href = "http://localhost:3000/login";
  };

  const handleMenu = () => {
    window.location.href = "http://localhost:3000/menu";
  };

  return (
    <div className="app-container">
      {/* Menú Lateral */}
      <aside className="sidebar">
        <div>
          <h2 className="sidebar-title">Opciones</h2>
          <button onClick={handleMenu} className="sidebar-button">
            Menú Principal
          </button>
          <button onClick={handleLogout} className="sidebar-button">
            Cerrar Sesión
          </button>
        </div>
      </aside>

      {/* Contenedor Principal */}
      <div className="content-container">
        <div className="chatbot-container">
        
          {nombreUsuario && (
            <p className="chatbot-welcome">
              ¡Hola <strong>{nombreUsuario}</strong>! ¿En qué puedo ayudarte hoy?
            </p>
          )}

          {/* Caja de Mensajes */}
          <div className="chat-box">
            {conversation.map((msg, index) => (
              <div
                key={index}
                className={`${
                  msg.sender === nombreUsuario
                    ? "chat-message-user"
                    : "chat-message-bot"
                }`}
              >
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>

          {/* Entrada de Mensajes */}
          <form onSubmit={generateText} className="chat-input-container">
            <input
              type="text"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Escribe tu mensaje..."
              className="custom-input"
              required
              disabled={isLoading}
            />
            <button
              type="submit"
              className="custom-button"
              disabled={isLoading}
            >
              {isLoading ? "Cargando..." : "Enviar"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default GeneradorTextoChatbot;
