import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Register.css"; // Archivo CSS para personalización adicional

function Register() {
  const [name, setName] = useState("");
  const [lastName, setLastName] = useState(""); // Campo para apellidos
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");

    // Concatenar nombre y apellidos
    const fullName = `${name} ${lastName}`.trim();

    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: fullName, email, password }), // Enviar fullName al servidor
      });

      const data = await response.json();
      if (data.success) {
        alert("Registro exitoso. Ahora puedes iniciar sesión.");
        navigate("/login"); // Redirige al login después de registrarse
      } else {
        setError(data.message || "Error al registrarse");
      }
    } catch (err) {
      setError("Error al conectarse al servidor");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="register-container flex items-center justify-center min-h-screen bg-gradient-to-br from-gray-100 via-gray-300 to-gray-200">
      {/* Contenedor principal */}
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-semibold text-gray-800 text-center mb-6">
          Registrarse
        </h2>

        {/* Mensaje de error */}
        {error && (
          <p className="text-red-600 text-sm text-center mb-4">{error}</p>
        )}

        {/* Formulario */}
        <form onSubmit={handleRegister} className="space-y-4">
          <input
            type="text"
            placeholder="Nombre"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="register-input w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <input
            type="text"
            placeholder="Apellidos"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            className="register-input w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <input
            type="email"
            placeholder="Correo"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="register-input w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="register-input w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            required
          />
          <button
            type="submit"
            className={`w-full py-2 text-white font-bold rounded-md ${
              isLoading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-indigo-600 hover:bg-indigo-700"
            }`}
            disabled={isLoading}
          >
            {isLoading ? "Cargando..." : "Registrarse"}
          </button>
        </form>

        {/* Botón de volver */}
        <button
          onClick={() => navigate("/login")}
          className="w-full mt-4 py-2 text-gray-800 font-bold rounded-md bg-gray-300 hover:bg-gray-400"
        >
          Volver al Login
        </button>
      </div>
    </div>
  );
}

export default Register;
