import React, { useState, useEffect } from "react";
import { FaHome, FaSignOutAlt, FaUserEdit, FaTrashAlt, FaLock } from "react-icons/fa";
import "../styles/gestionar_usuario.css";

function GestionarUsuario() {
    const [usuario, setUsuario] = useState({ nombre: "", email: "" });
    const [password, setPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [modalContent, setModalContent] = useState({ message: "", type: "" });

    useEffect(() => {
        const fetchUsuario = async () => {
            const idSesion = localStorage.getItem("id_sesion");
            if (!idSesion) {
                showModal("No se encontró una sesión activa", "error");
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/usuario?id_sesion=${idSesion}`);
                const data = await response.json();

                if (response.ok) {
                    setUsuario(data.usuario);
                } else {
                    showModal(data.error || "Error al obtener datos del usuario", "error");
                }
            } catch (err) {
                showModal("Error al conectar con el servidor", "error");
            }
        };

        fetchUsuario();
    }, []);

    const handleUpdate = async (e) => {
        e.preventDefault();
    
        try {
            const response = await fetch("http://localhost:5000/usuario", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ ...usuario, id_sesion: localStorage.getItem("id_sesion") }),
            });
    
            if (response.ok) {
                showModal("Datos actualizados correctamente", "success");
            } else {
                const data = await response.json();
                if (response.status === 409) {
                    // Caso específico: Correo duplicado
                    showModal(data.error || "El correo ya está registrado con otro usuario", "error");
                } else {
                    // Otros errores
                    showModal(data.error || "Error al actualizar los datos", "error");
                }
            }
        } catch (err) {
            showModal("Error al conectar con el servidor", "error");
        }
    };

    const handleUpdatePassword = async (e) => {
        e.preventDefault();

        if (newPassword !== confirmPassword) {
            showModal("Las contraseñas no coinciden", "error");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/usuario", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    id_sesion: localStorage.getItem("id_sesion"),
                    password: password,
                    new_password: newPassword,
                }),
            });

            if (response.ok) {
                showModal("Contraseña actualizada correctamente", "success");
                setPassword("");
                setNewPassword("");
                setConfirmPassword("");
            } else {
                const data = await response.json();
                showModal(data.error || "Error al actualizar la contraseña", "error");
            }
        } catch (err) {
            showModal("Error al conectar con el servidor", "error");
        }
    };

    const handleDelete = async () => {
        if (window.confirm("¿Estás seguro de que deseas eliminar tu cuenta? Esta acción no se puede deshacer.")) {
            try {
                const idSesion = localStorage.getItem("id_sesion");
                const response = await fetch(`http://localhost:5000/usuario?id_sesion=${idSesion}`, {
                    method: "DELETE",
                });

                if (response.ok) {
                    localStorage.clear();
                    window.location.href = "/login";
                } else {
                    const data = await response.json();
                    showModal(data.error || "Error al eliminar la cuenta", "error");
                }
            } catch (err) {
                showModal("Error al conectar con el servidor", "error");
            }
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("id_sesion");
        window.location.href = "/login";
    };

    const handleMenu = () => {
        window.location.href = "/menu_usuario";
    };

    const showModal = (message, type) => {
        setModalContent({ message, type });
        setIsModalVisible(true);
        setTimeout(() => {
            setIsModalVisible(false);
        }, 3000);
    };

    return (
        <div className="gestionar-usuario-container flex flex-col lg:flex-row">
            {/* Menú lateral */}
            <aside className="sidebar bg-gray-800 text-white p-4 lg:w-1/4 h-full">
                <h2 className="sidebar-title text-lg font-bold mb-4">Opciones</h2>
                <button onClick={handleMenu} className="sidebar-button flex items-center bg-gray-700 p-2 rounded mb-2 hover:bg-gray-600">
                    <FaHome className="mr-2" /> Menú Principal
                </button>
                <button onClick={handleLogout} className="sidebar-button flex items-center bg-gray-700 p-2 rounded hover:bg-gray-600">
                    <FaSignOutAlt className="mr-2" /> Cerrar Sesión
                </button>
            </aside>
    
            {/* Contenido Principal */}
            <div className="usuario-box flex-1 bg-gray-100 p-6">
                <h1 className="usuario-title text-2xl font-bold text-gray-800 mb-6 text-center">Gestionar Mi Cuenta</h1>
    
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {/* Formulario para actualizar datos */}
                    <div className="bg-white p-6 rounded shadow">
                        <h2 className="text-xl font-semibold mb-4">Actualizar Datos</h2>
                        <form onSubmit={handleUpdate} className="usuario-form">
                            <label className="block mb-4">
                                <span className="text-gray-700">Nombre:</span>
                                <input
                                    type="text"
                                    value={usuario.nombre}
                                    onChange={(e) => setUsuario({ ...usuario, nombre: e.target.value })}
                                    className="mt-1 block w-full rounded border-gray-300 shadow-sm"
                                    required
                                />
                            </label>
                            <label className="block mb-4">
                                <span className="text-gray-700">Email:</span>
                                <input
                                    type="email"
                                    value={usuario.email}
                                    onChange={(e) => setUsuario({ ...usuario, email: e.target.value })}
                                    className="mt-1 block w-full rounded border-gray-300 shadow-sm"
                                    required
                                />
                            </label>
                            <button
                                type="submit"
                                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 flex items-center justify-center"
                            >
                                <FaUserEdit className="mr-2" /> Actualizar Datos
                            </button>
                        </form>
                    </div>
    
                    {/* Formulario para cambiar contraseña */}
                    <div className="bg-white p-6 rounded shadow">
                        <h2 className="text-xl font-semibold mb-4">Cambiar Contraseña</h2>
                        <form onSubmit={handleUpdatePassword} className="password-form">
                            <label className="block mb-4">
                                <span className="text-gray-700">Contraseña Actual:</span>
                                <input
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="mt-1 block w-full rounded border-gray-300 shadow-sm"
                                    required
                                />
                            </label>
                            <label className="block mb-4">
                                <span className="text-gray-700">Nueva Contraseña:</span>
                                <input
                                    type="password"
                                    value={newPassword}
                                    onChange={(e) => setNewPassword(e.target.value)}
                                    className="mt-1 block w-full rounded border-gray-300 shadow-sm"
                                    required
                                />
                            </label>
                            <label className="block mb-4">
                                <span className="text-gray-700">Confirmar Nueva Contraseña:</span>
                                <input
                                    type="password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    className="mt-1 block w-full rounded border-gray-300 shadow-sm"
                                    required
                                />
                            </label>
                            <button
                                type="submit"
                                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 flex items-center justify-center"
                            >
                                <FaLock className="mr-2" /> Cambiar Contraseña
                            </button>
                        </form>
                    </div>
                </div>
    
                {/* Botón para eliminar cuenta */}
                <div className="mt-6 text-center">
                    <button
                        onClick={handleDelete}
                        className="bg-red-500 text-white px-6 py-2 rounded hover:bg-red-600 flex items-center justify-center mx-auto"
                    >
                        <FaTrashAlt className="mr-2" /> Eliminar Cuenta
                    </button>
                </div>
            </div>
        </div>
    );
    
}

export default GestionarUsuario;
