import React, { useState, useEffect } from "react";
import { FaHome, FaSignOutAlt, FaUserEdit, FaTrashAlt, FaLock } from "react-icons/fa";
import "../styles/gestionar_usuario.css"; // Estilo personalizado

function GestionarUsuario() {
    const [usuario, setUsuario] = useState({ nombre: "", email: "" });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");
    const [password, setPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    useEffect(() => {
        const fetchUsuario = async () => {
            const idSesion = localStorage.getItem("id_sesion");
            if (!idSesion) {
                setError("No se encontró una sesión activa");
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/usuario?id_sesion=${idSesion}`);
                const data = await response.json();

                if (response.ok) {
                    setUsuario(data.usuario);
                } else {
                    setError(data.message || "Error al obtener datos del usuario");
                }
            } catch (err) {
                setError("Error al conectar con el servidor");
            } finally {
                setLoading(false);
            }
        };

        fetchUsuario();
    }, []);

    const handleUpdatePassword = async (e) => {
        e.preventDefault();
        setError("");
        setSuccess("");

        if (newPassword !== confirmPassword) {
            setError("Las contraseñas no coinciden.");
            return;
        }

        try {
            const idSesion = localStorage.getItem("id_sesion");
            const response = await fetch("http://localhost:5000/usuario/password", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ id_sesion: idSesion, password, new_password: newPassword }),
            });

            if (response.ok) {
                setSuccess("Contraseña actualizada correctamente.");
                setPassword("");
                setNewPassword("");
                setConfirmPassword("");
            } else {
                const data = await response.json();
                setError(data.message || "Error al actualizar la contraseña.");
            }
        } catch (err) {
            setError("Error al conectar con el servidor.");
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("id_sesion");
        window.location.href = "/login";
    };

    const handleMenu = () => {
        window.location.href = "/menu_usuario";
    };

    if (loading) return <p>Cargando...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div className="gestionar-usuario-container">
            {/* Menú lateral */}
            <aside className="sidebar">
                <h2 className="sidebar-title">Opciones</h2>
                <button onClick={handleMenu} className="sidebar-button">
                    <FaHome className="sidebar-icon" /> Menú Principal
                </button>
                <button onClick={handleLogout} className="sidebar-button">
                    <FaSignOutAlt className="sidebar-icon" /> Cerrar Sesión
                </button>
            </aside>

            {/* Formulario de Gestión */}
            <div className="usuario-box">
                <h1 className="usuario-title">Gestionar Usuario</h1>

                {success && <p className="success-message">{success}</p>}

                {/* Formulario para cambiar contraseña */}
                <form onSubmit={handleUpdatePassword} className="password-form">
                    <label>
                        Contraseña Actual:
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Nueva Contraseña:
                        <input
                            type="password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Confirmar Nueva Contraseña:
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                        />
                    </label>
                    <button type="submit" className="usuario-button">
                        <FaLock /> Cambiar Contraseña
                    </button>
                </form>
            </div>
        </div>
    );
}

export default GestionarUsuario;
