import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Estado para manejar errores
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log("Login iniciado");
    const dispositivo = navigator.userAgent; // Información del dispositivo
    const canal = "web"; // Canal fijo como ejemplo
    console.log("Enviando datos:", { email, password, dispositivo, canal });
    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, dispositivo, canal }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Error en el servidor");
      }

      const data = await response.json();
      if (data.success) {
        // Guarda la sesión del usuario
        localStorage.setItem("id_sesion", data.id_sesion);
        localStorage.setItem("nombre_usuario", data.nombre);
  
        // Redirige al frontend del chatbot (puerto 3002)
        window.location.href = `http://localhost:3002?session_id=${data.id_sesion}&nombre_usuario=${encodeURIComponent(data.nombre)}`;
      } else {
        setError(data.message || "Credenciales incorrectas");
      }
    } catch (err) {
      console.error("Error al iniciar sesión:", err); // Depuración
      setError(err.message || "Error al iniciar sesión");
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {/* Mostrar mensajes de error */}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Correo"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
          required
        />
        <button type="submit">Iniciar Sesión</button>
      </form>
      <button onClick={() => navigate("/register")}>Registrarse</button>
    </div>
  );
}

export default Login;
