import React, { useState, useEffect } from "react";

function MisInteracciones() {
    const [interacciones, setInteracciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchInteracciones = async () => {
            const idSesion = localStorage.getItem("id_sesion");
            if (!idSesion) {
                setError("No se encontró una sesión activa");
                setLoading(false);
                return;
            }

            try {
                // Llamar al endpoint unificado
                const response = await fetch(`http://localhost:5000/mis-interacciones?id_sesion=${idSesion}`);
                const data = await response.json();

                if (response.ok) {
                    // Procesar las interacciones en orden cronológico
                    const ordenado = data.interacciones.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
                    setInteracciones(ordenado);
                } else {
                    setError(data.message || "Error al obtener interacciones");
                }
            } catch (err) {
                setError("Error al conectar con el servidor");
            } finally {
                setLoading(false);
            }
        };

        fetchInteracciones();
    }, []);

    if (loading) return <p>Cargando...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h1>Mis Interacciones</h1>
            <table border="1" style={{ width: "100%", textAlign: "left", borderCollapse: "collapse" }}>
                <thead>
                    <tr>
                        <th>Remitente</th>
                        <th>Mensaje</th>
                        <th>Timestamp</th>
                    </tr>
                </thead>
                <tbody>
                    {interacciones.map((interaccion, index) => (
                        <tr key={index}>
                            <td>{interaccion.tipo_mensaje === "Chatbot" ? "Chatbot" : "Tú"}</td>
                            <td>{interaccion.mensaje}</td>
                            <td>{new Date(interaccion.timestamp).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default MisInteracciones;
