import React, { useState, useEffect } from "react";
import "./GeneradorTextoChatbot.css"; // Archivo de estilos personalizados

function GeneradorTextoChatbot() {
  const [prompt, setPrompt] = useState("");
  const [conversation, setConversation] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [sessionId, setSessionId] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");

  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const session_id = queryParams.get("session_id");
    const nombre = queryParams.get("nombre_usuario");
    if (session_id) {
      setSessionId(session_id);
    } else {
      setErrorMessage("No se encontr贸 una sesi贸n activa.");
      window.location.href = "http://localhost:3000/login";
    }
    if (nombre) {
      setNombreUsuario(nombre);
    }
  }, []);

  const generateText = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await fetch("http://localhost:5000/api/chatbot", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          prompt,
          id_sesion: sessionId,
          nombre_usuario: nombreUsuario,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.error || "Error desconocido en el servidor");
        return;
      }

      const data = await response.json();
      const respuestaChatGPT = data.response;

      setConversation((prev) => [
        ...prev,
        { sender: nombreUsuario, text: prompt },
        { sender: "Chatbot", text: respuestaChatGPT },
      ]);
    } catch (error) {
      setErrorMessage("Error en la solicitud al servidor");
    } finally {
      setIsLoading(false);
      setPrompt("");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("id_sesion");
    localStorage.removeItem("nombre_usuario");
    window.location.href = "http://localhost:3000/login";
  };

  const handleMenu = () => {
    window.location.href = "http://localhost:3000/menu";
  };

  return (
    <div className="flex min-h-screen">
      {/* Barra Lateral */}
      <aside className="sidebar bg-blue-900 text-white w-64 flex flex-col justify-between">
        <div className="p-6">
          <h2 className="text-xl font-bold mb-4">Opciones</h2>
          <button
            onClick={handleMenu}
            className="w-full text-left px-4 py-2 hover:bg-blue-700 rounded"
          >
            Regresar al Men煤 Principal
          </button>
          <button
            onClick={handleLogout}
            className="w-full text-left px-4 py-2 hover:bg-blue-700 rounded mt-2"
          >
            Cerrar Sesi贸n
          </button>
        </div>
      </aside>

      {/* Contenido Principal */}
      <div className="flex-grow bg-gradient-to-br from-gray-100 to-gray-300 flex items-center justify-center">
        <div className="chatbot-container bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl">
          <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">
            Asistente Virtual
          </h2>
          {nombreUsuario && (
            <p className="text-center text-gray-600 mb-4">
              隆Hola <strong>{nombreUsuario}</strong>! 驴En qu茅 puedo ayudarte hoy?
            </p>
          )}

          {/* Caja de Mensajes */}
          <div className="chat-box bg-gray-100 h-96 overflow-y-auto rounded-lg p-4 mb-4">
            {conversation.map((msg, index) => (
              <div
                key={index}
                className={`${
                  msg.sender === nombreUsuario
                    ? "chat-message-user"
                    : "chat-message-bot"
                }`}
              >
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>

          {/* Entrada de Texto */}
          <form onSubmit={generateText} className="flex items-center gap-2">
            <input
              type="text"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Escribe tu mensaje..."
              className="flex-grow px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
              disabled={isLoading}
            />
            <button
              type="submit"
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg"
              disabled={isLoading}
            >
              {isLoading ? "Cargando..." : "Enviar"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default GeneradorTextoChatbot;
